import{_ as o,c as e,o as s,ag as r}from"./chunks/framework.BgN_SlIJ.js";const m=JSON.parse('{"title":"Notas de Versão - v1.0.0","description":"","frontmatter":{},"headers":[],"relativePath":"release-notes/v1.0.0.md","filePath":"release-notes/v1.0.0.md","lastUpdated":null}'),i={name:"release-notes/v1.0.0.md"};function t(n,a,l,d,c,g){return s(),e("div",null,[...a[0]||(a[0]=[r('<h1 id="notas-de-versao-v1-0-0" tabindex="-1">Notas de Versão - v1.0.0 <a class="header-anchor" href="#notas-de-versao-v1-0-0" aria-label="Permalink to &quot;Notas de Versão - v1.0.0&quot;">​</a></h1><p><strong>Data de lançamento:</strong> Janeiro 2025<br><strong>Tipo:</strong> Lançamento inicial<br><strong>Compatibilidade:</strong> Nova instalação</p><h2 id="🎉-bem-vindos-ao-nextgoup" tabindex="-1">🎉 Bem-vindos ao Nextgoup! <a class="header-anchor" href="#🎉-bem-vindos-ao-nextgoup" aria-label="Permalink to &quot;🎉 Bem-vindos ao Nextgoup!&quot;">​</a></h2><p>Esta é a primeira versão oficial do sistema Nextgoup para clientes. Após meses de desenvolvimento e testes, estamos orgulhosos de apresentar uma plataforma completa de gestão empresarial.</p><h2 id="✨-principais-funcionalidades" tabindex="-1">✨ Principais funcionalidades <a class="header-anchor" href="#✨-principais-funcionalidades" aria-label="Permalink to &quot;✨ Principais funcionalidades&quot;">​</a></h2><h3 id="🏠-dashboard-inteligente" tabindex="-1">🏠 Dashboard Inteligente <a class="header-anchor" href="#🏠-dashboard-inteligente" aria-label="Permalink to &quot;🏠 Dashboard Inteligente&quot;">​</a></h3><ul><li><strong>Cards de métricas</strong> personalizáveis por perfil de usuário</li><li><strong>Gráficos interativos</strong> com drill-down para análises detalhadas</li><li><strong>Notificações em tempo real</strong> para alertas importantes</li><li><strong>Widgets arrastar-e-soltar</strong> para personalização do layout</li><li><strong>Modo claro/escuro</strong> automático baseado em preferências</li></ul><h3 id="👥-gestao-de-usuarios" tabindex="-1">👥 Gestão de Usuários <a class="header-anchor" href="#👥-gestao-de-usuarios" aria-label="Permalink to &quot;👥 Gestão de Usuários&quot;">​</a></h3><ul><li><strong>Três perfis distintos:</strong> Administrador, Gestor e Operador</li><li><strong>Sistema de permissões granular</strong> por módulo e funcionalidade</li><li><strong>Autenticação segura</strong> com políticas de senha configuráveis</li><li><strong>Controle de sessão</strong> com timeout automático</li><li><strong>Logs de auditoria</strong> completos para todas as ações</li></ul><h3 id="🛍️-modulo-de-produtos" tabindex="-1">🛍️ Módulo de Produtos <a class="header-anchor" href="#🛍️-modulo-de-produtos" aria-label="Permalink to &quot;🛍️ Módulo de Produtos&quot;">​</a></h3><ul><li><strong>Catálogo organizado</strong> por categorias hierárquicas</li><li><strong>Controle de estoque</strong> com alertas de níveis mínimos</li><li><strong>Galeria de imagens</strong> para cada produto</li><li><strong>Importação em lote</strong> via Excel/CSV</li><li><strong>Análise ABC</strong> automática para classificação de produtos</li></ul><h3 id="📊-sistema-de-relatorios" tabindex="-1">📊 Sistema de Relatórios <a class="header-anchor" href="#📊-sistema-de-relatorios" aria-label="Permalink to &quot;📊 Sistema de Relatórios&quot;">​</a></h3><ul><li><strong>30+ relatórios pré-configurados</strong> cobrindo todas as áreas</li><li><strong>Construtor drag-and-drop</strong> para relatórios personalizados</li><li><strong>Exportação múltipla:</strong> PDF, Excel, CSV, JSON</li><li><strong>Agendamento automático</strong> com distribuição por e-mail</li><li><strong>Dashboards interativos</strong> com visualizações avançadas</li></ul><h3 id="✅-fluxo-de-aprovacoes" tabindex="-1">✅ Fluxo de Aprovações <a class="header-anchor" href="#✅-fluxo-de-aprovacoes" aria-label="Permalink to &quot;✅ Fluxo de Aprovações&quot;">​</a></h3><ul><li><strong>Workflow configurável</strong> por tipo de solicitação</li><li><strong>Múltiplos níveis</strong> de aprovação quando necessário</li><li><strong>Histórico completo</strong> de decisões e justificativas</li><li><strong>Notificações automáticas</strong> para todas as partes envolvidas</li><li><strong>Delegação temporária</strong> durante ausências</li></ul><h2 id="🔧-melhorias-tecnicas" tabindex="-1">🔧 Melhorias técnicas <a class="header-anchor" href="#🔧-melhorias-tecnicas" aria-label="Permalink to &quot;🔧 Melhorias técnicas&quot;">​</a></h2><h3 id="performance" tabindex="-1">Performance <a class="header-anchor" href="#performance" aria-label="Permalink to &quot;Performance&quot;">​</a></h3><ul><li><strong>Interface responsiva</strong> otimizada para desktop e mobile</li><li><strong>Cache inteligente</strong> para consultas frequentes</li><li><strong>Compressão automática</strong> de imagens upload</li><li><strong>Lazy loading</strong> para listas grandes</li><li><strong>Otimização de queries</strong> no banco de dados</li></ul><h3 id="seguranca" tabindex="-1">Segurança <a class="header-anchor" href="#seguranca" aria-label="Permalink to &quot;Segurança&quot;">​</a></h3><ul><li><strong>Criptografia SSL/TLS</strong> em todas as comunicações</li><li><strong>Hash bcrypt</strong> para senhas armazenadas</li><li><strong>Token JWT</strong> para autenticação de sessões</li><li><strong>Rate limiting</strong> contra ataques de força bruta</li><li><strong>Logs de segurança</strong> detalhados</li></ul><h3 id="usabilidade" tabindex="-1">Usabilidade <a class="header-anchor" href="#usabilidade" aria-label="Permalink to &quot;Usabilidade&quot;">​</a></h3><ul><li><strong>Busca global rápida</strong> (Ctrl+K) em todos os módulos</li><li><strong>Atalhos de teclado</strong> para ações frequentes</li><li><strong>Tooltips contextuais</strong> para orientação</li><li><strong>Mensagens de erro</strong> claras e acionáveis</li><li><strong>Navegação breadcrumb</strong> intuitiva</li></ul><h2 id="📱-compatibilidade" tabindex="-1">📱 Compatibilidade <a class="header-anchor" href="#📱-compatibilidade" aria-label="Permalink to &quot;📱 Compatibilidade&quot;">​</a></h2><h3 id="navegadores-suportados" tabindex="-1">Navegadores suportados <a class="header-anchor" href="#navegadores-suportados" aria-label="Permalink to &quot;Navegadores suportados&quot;">​</a></h3><ul><li>✅ <strong>Chrome 100+</strong> (Recomendado)</li><li>✅ <strong>Firefox 90+</strong></li><li>✅ <strong>Safari 14+</strong></li><li>✅ <strong>Edge 100+</strong></li><li>⚠️ <strong>Internet Explorer:</strong> Não suportado</li></ul><h3 id="dispositivos-moveis" tabindex="-1">Dispositivos móveis <a class="header-anchor" href="#dispositivos-moveis" aria-label="Permalink to &quot;Dispositivos móveis&quot;">​</a></h3><ul><li>✅ <strong>iOS 14+</strong> (Safari, Chrome)</li><li>✅ <strong>Android 10+</strong> (Chrome, Samsung Internet)</li><li>✅ <strong>Tablets:</strong> Interface adaptada para touch</li></ul><h3 id="resolucoes-de-tela" tabindex="-1">Resoluções de tela <a class="header-anchor" href="#resolucoes-de-tela" aria-label="Permalink to &quot;Resoluções de tela&quot;">​</a></h3><ul><li>✅ <strong>Desktop:</strong> 1280x720 mínimo, 1920x1080 recomendado</li><li>✅ <strong>Tablet:</strong> 768x1024 ou superior</li><li>✅ <strong>Mobile:</strong> 375x667 ou superior</li></ul><h2 id="🎯-impacto-para-usuarios" tabindex="-1">🎯 Impacto para usuários <a class="header-anchor" href="#🎯-impacto-para-usuarios" aria-label="Permalink to &quot;🎯 Impacto para usuários&quot;">​</a></h2><h3 id="para-administradores" tabindex="-1">Para Administradores <a class="header-anchor" href="#para-administradores" aria-label="Permalink to &quot;Para Administradores&quot;">​</a></h3><ul><li><strong>Controle total</strong> sobre configuração e usuários</li><li><strong>Dashboards executivos</strong> com métricas consolidadas</li><li><strong>Ferramentas de auditoria</strong> para compliance</li><li><strong>Gestão proativa</strong> com alertas automáticos</li></ul><p><strong>⏱️ Treinamento recomendado:</strong> 4-6 horas</p><h3 id="para-gestores" tabindex="-1">Para Gestores <a class="header-anchor" href="#para-gestores" aria-label="Permalink to &quot;Para Gestores&quot;">​</a></h3><ul><li><strong>Visão departamental</strong> clara e focada</li><li><strong>Aprovações ágeis</strong> com contexto completo</li><li><strong>Relatórios gerenciais</strong> automáticos</li><li><strong>Acompanhamento de equipe</strong> em tempo real</li></ul><p><strong>⏱️ Treinamento recomendado:</strong> 2-3 horas</p><h3 id="para-operadores" tabindex="-1">Para Operadores <a class="header-anchor" href="#para-operadores" aria-label="Permalink to &quot;Para Operadores&quot;">​</a></h3><ul><li><strong>Interface simplificada</strong> focada em produtividade</li><li><strong>Fluxos guiados</strong> para tarefas complexas</li><li><strong>Acesso rápido</strong> a informações necessárias</li><li><strong>Relatórios pessoais</strong> de atividade</li></ul><p><strong>⏱️ Treinamento recomendado:</strong> 1-2 horas</p><h2 id="📚-recursos-de-apoio" tabindex="-1">📚 Recursos de apoio <a class="header-anchor" href="#📚-recursos-de-apoio" aria-label="Permalink to &quot;📚 Recursos de apoio&quot;">​</a></h2><h3 id="documentacao" tabindex="-1">Documentação <a class="header-anchor" href="#documentacao" aria-label="Permalink to &quot;Documentação&quot;">​</a></h3><ul><li>✅ <strong>Manual completo</strong> disponível em <code>/client-docs</code></li><li>✅ <strong>FAQ abrangente</strong> com 50+ perguntas</li><li>✅ <strong>Guias passo-a-passo</strong> para todas as tarefas</li><li>✅ <strong>Tutoriais em vídeo</strong> (em produção)</li></ul><h3 id="suporte" tabindex="-1">Suporte <a class="header-anchor" href="#suporte" aria-label="Permalink to &quot;Suporte&quot;">​</a></h3><ul><li>✅ <strong>Ajuda contextual</strong> integrada ao sistema</li><li>✅ <strong>Chat de suporte</strong> durante horário comercial</li><li>✅ <strong>Base de conhecimento</strong> pesquisável</li><li>✅ <strong>Fórum de usuários</strong> para troca de experiências</li></ul><h2 id="🔄-integracoes-disponiveis" tabindex="-1">🔄 Integrações disponíveis <a class="header-anchor" href="#🔄-integracoes-disponiveis" aria-label="Permalink to &quot;🔄 Integrações disponíveis&quot;">​</a></h2><h3 id="sistemas-empresariais" tabindex="-1">Sistemas empresariais <a class="header-anchor" href="#sistemas-empresariais" aria-label="Permalink to &quot;Sistemas empresariais&quot;">​</a></h3><ul><li><strong>ERP Financeiro:</strong> Sincronização bidirectional de dados</li><li><strong>CRM Vendas:</strong> Importação de leads e clientes</li><li><strong>E-commerce:</strong> Catálogo e gestão de pedidos</li><li><strong>RH/LDAP:</strong> Autenticação corporativa centralizada</li></ul><h3 id="ferramentas-de-bi" tabindex="-1">Ferramentas de BI <a class="header-anchor" href="#ferramentas-de-bi" aria-label="Permalink to &quot;Ferramentas de BI&quot;">​</a></h3><ul><li><strong>Power BI:</strong> Conectores nativos para dashboards</li><li><strong>Tableau:</strong> Exportação direta de datasets</li><li><strong>Excel:</strong> Integração avançada com tabelas dinâmicas</li><li><strong>Google Sheets:</strong> APIs para sincronização automática</li></ul><h2 id="🛣️-proximos-passos" tabindex="-1">🛣️ Próximos passos <a class="header-anchor" href="#🛣️-proximos-passos" aria-label="Permalink to &quot;🛣️ Próximos passos&quot;">​</a></h2><h3 id="v1-1-0-marco-2025" tabindex="-1">v1.1.0 (Março 2025) <a class="header-anchor" href="#v1-1-0-marco-2025" aria-label="Permalink to &quot;v1.1.0 (Março 2025)&quot;">​</a></h3><ul><li><strong>App mobile nativo</strong> para iOS e Android</li><li><strong>Módulo de CRM</strong> integrado</li><li><strong>Workflow designer</strong> visual</li><li><strong>Relatórios em tempo real</strong> com websockets</li><li><strong>API pública</strong> para integrações customizadas</li></ul><h3 id="v1-2-0-junho-2025" tabindex="-1">v1.2.0 (Junho 2025) <a class="header-anchor" href="#v1-2-0-junho-2025" aria-label="Permalink to &quot;v1.2.0 (Junho 2025)&quot;">​</a></h3><ul><li><strong>Inteligência artificial</strong> para insights automáticos</li><li><strong>Módulo financeiro</strong> completo</li><li><strong>Gestão de projetos</strong> integrada</li><li><strong>Colaboração em tempo real</strong> em documentos</li><li><strong>Multi-idioma</strong> (Inglês, Espanhol)</li></ul><h2 id="⚠️-limitacoes-conhecidas" tabindex="-1">⚠️ Limitações conhecidas <a class="header-anchor" href="#⚠️-limitacoes-conhecidas" aria-label="Permalink to &quot;⚠️ Limitações conhecidas&quot;">​</a></h2><h3 id="funcionalidades" tabindex="-1">Funcionalidades <a class="header-anchor" href="#funcionalidades" aria-label="Permalink to &quot;Funcionalidades&quot;">​</a></h3><ul><li><strong>Relatórios muito complexos</strong> podem ter timeout (&gt;2 min)</li><li><strong>Upload simultâneo</strong> limitado a 5 arquivos</li><li><strong>Exportação de dados</strong> limitada a 50MB por arquivo</li><li><strong>Sessões longas</strong> (&gt;4h) podem apresentar instabilidade</li></ul><h3 id="integracoes" tabindex="-1">Integrações <a class="header-anchor" href="#integracoes" aria-label="Permalink to &quot;Integrações&quot;">​</a></h3><ul><li><strong>APIs externas</strong> podem ter latência em horários de pico</li><li><strong>Sincronização</strong> com ERPs legados pode demorar até 15 min</li><li><strong>Backup automático</strong> executa apenas durante madrugada</li></ul><h2 id="🔐-consideracoes-de-seguranca" tabindex="-1">🔐 Considerações de segurança <a class="header-anchor" href="#🔐-consideracoes-de-seguranca" aria-label="Permalink to &quot;🔐 Considerações de segurança&quot;">​</a></h2><h3 id="politicas-implementadas" tabindex="-1">Políticas implementadas <a class="header-anchor" href="#politicas-implementadas" aria-label="Permalink to &quot;Políticas implementadas&quot;">​</a></h3><ul><li><strong>Senhas:</strong> Mínimo 8 caracteres com complexidade</li><li><strong>Sessões:</strong> Timeout após 2h de inatividade</li><li><strong>Tentativas:</strong> Bloqueio após 5 logins incorretos</li><li><strong>Auditoria:</strong> Log completo de ações sensíveis</li><li><strong>Backup:</strong> Criptografado e testado semanalmente</li></ul><h3 id="lgpd-e-compliance" tabindex="-1">LGPD e Compliance <a class="header-anchor" href="#lgpd-e-compliance" aria-label="Permalink to &quot;LGPD e Compliance&quot;">​</a></h3><ul><li><strong>Consentimento:</strong> Explícito para tratamento de dados</li><li><strong>Portabilidade:</strong> Exportação de dados pessoais disponível</li><li><strong>Exclusão:</strong> Remoção completa mediante solicitação</li><li><strong>Auditoria:</strong> Logs de acesso a dados pessoais</li><li><strong>DPO:</strong> Encarregado designado e disponível</li></ul><h2 id="📞-suporte-e-contatos" tabindex="-1">📞 Suporte e contatos <a class="header-anchor" href="#📞-suporte-e-contatos" aria-label="Permalink to &quot;📞 Suporte e contatos&quot;">​</a></h2><h3 id="durante-a-implantacao" tabindex="-1">Durante a implantação <a class="header-anchor" href="#durante-a-implantacao" aria-label="Permalink to &quot;Durante a implantação&quot;">​</a></h3><ul><li><strong>Suporte técnico:</strong> 8h-18h, dias úteis</li><li><strong>Chat online:</strong> Disponível no sistema</li><li><strong>E-mail:</strong> <a href="mailto:suporte@nextgoup.com" target="_blank" rel="noreferrer">suporte@nextgoup.com</a></li><li><strong>Telefone:</strong> +55 (11) 3000-0000</li></ul><h3 id="treinamentos" tabindex="-1">Treinamentos <a class="header-anchor" href="#treinamentos" aria-label="Permalink to &quot;Treinamentos&quot;">​</a></h3><ul><li><strong>Sessões coletivas:</strong> Agendadas conforme demanda</li><li><strong>Treinamento 1:1:</strong> Para administradores</li><li><strong>Materiais online:</strong> Disponíveis 24/7</li><li><strong>Certificação:</strong> Programa em desenvolvimento</li></ul><hr><h2 id="🎊-agradecimentos" tabindex="-1">🎊 Agradecimentos <a class="header-anchor" href="#🎊-agradecimentos" aria-label="Permalink to &quot;🎊 Agradecimentos&quot;">​</a></h2><p>Agradecemos a todos os usuários beta que contribuíram com feedback valioso durante o desenvolvimento. Esta versão incorpora mais de 200 sugestões de melhoria coletadas durante os testes.</p><p><strong>Equipe de desenvolvimento Nextgoup</strong><br> Janeiro 2025</p><hr><div class="tip custom-block"><p class="custom-block-title">Próxima versão</p><p>Fique atento às novas funcionalidades que estão sendo desenvolvidas para a próxima versão.</p></div><p><strong>Precisa de ajuda com esta versão?</strong> Consulte nosso <a href="/nextgoup/faq/">FAQ completo</a> ou entre em contato com o suporte.</p>',76)])])}const p=o(i,[["render",t]]);export{m as __pageData,p as default};
