import{_ as a,c as r,o as e,ag as i}from"./chunks/framework.BgN_SlIJ.js";const m=JSON.parse('{"title":"Gerar Relatório","description":"","frontmatter":{},"headers":[],"relativePath":"tasks/gerar-relatorio.md","filePath":"tasks/gerar-relatorio.md","lastUpdated":null}'),s={name:"tasks/gerar-relatorio.md"};function t(l,o,n,d,u,c){return e(),r("div",null,[...o[0]||(o[0]=[i('<h1 id="gerar-relatorio" tabindex="-1">Gerar Relatório <a class="header-anchor" href="#gerar-relatorio" aria-label="Permalink to &quot;Gerar Relatório&quot;">​</a></h1><p>Aprenda a criar, personalizar e exportar relatórios no sistema Nextgoup.</p><h2 id="quem-pode-executar-esta-tarefa" tabindex="-1">Quem pode executar esta tarefa? <a class="header-anchor" href="#quem-pode-executar-esta-tarefa" aria-label="Permalink to &quot;Quem pode executar esta tarefa?&quot;">​</a></h2><ul><li>✅ <strong>Administrador:</strong> Todos os relatórios</li><li>✅ <strong>Gestor:</strong> Relatórios do seu departamento</li><li>✅ <strong>Operador:</strong> Relatórios básicos (conforme permissão)</li></ul><p><strong>Tempo médio de execução:</strong> 5-10 minutos</p><h2 id="passo-a-passo" tabindex="-1">Passo a passo <a class="header-anchor" href="#passo-a-passo" aria-label="Permalink to &quot;Passo a passo&quot;">​</a></h2><h3 id="_1-acessar-modulo-de-relatorios" tabindex="-1">1. Acessar módulo de relatórios <a class="header-anchor" href="#_1-acessar-modulo-de-relatorios" aria-label="Permalink to &quot;1. Acessar módulo de relatórios&quot;">​</a></h3><ol><li>No menu lateral, clique em <strong>&quot;Relatórios&quot;</strong></li><li>Escolha uma das categorias disponíveis: <ul><li><strong>Dashboard:</strong> Métricas gerais</li><li><strong>Vendas:</strong> Performance comercial</li><li><strong>Produtos:</strong> Estoque e movimentação</li><li><strong>Usuários:</strong> Atividade e acessos</li><li><strong>Financeiro:</strong> Dados monetários</li></ul></li></ol><h3 id="_2-selecionar-tipo-de-relatorio" tabindex="-1">2. Selecionar tipo de relatório <a class="header-anchor" href="#_2-selecionar-tipo-de-relatorio" aria-label="Permalink to &quot;2. Selecionar tipo de relatório&quot;">​</a></h3><p><strong>Relatórios disponíveis por categoria:</strong></p><table tabindex="0"><thead><tr><th>Categoria</th><th>Relatórios</th></tr></thead><tbody><tr><td><strong>Vendas</strong></td><td>Vendas por período, Top produtos, Performance vendedor</td></tr><tr><td><strong>Produtos</strong></td><td>Estoque atual, Produtos mais vendidos, Análise ABC</td></tr><tr><td><strong>Usuários</strong></td><td>Log de acessos, Usuários ativos, Relatório de permissões</td></tr><tr><td><strong>Financeiro</strong></td><td>Faturamento, Contas a receber, Margem de lucro</td></tr></tbody></table><h3 id="_3-configurar-filtros" tabindex="-1">3. Configurar filtros <a class="header-anchor" href="#_3-configurar-filtros" aria-label="Permalink to &quot;3. Configurar filtros&quot;">​</a></h3><p><strong>Filtros essenciais:</strong></p><ul><li><strong>Período:</strong> Data início e fim</li><li><strong>Departamento:</strong> Se aplicável ao seu perfil</li><li><strong>Status:</strong> Ativo, Inativo, Todos</li><li><strong>Categoria:</strong> Produtos, usuários, etc.</li></ul><p><strong>Filtros avançados:</strong></p><ul><li><strong>Cliente específico</strong></li><li><strong>Vendedor</strong></li><li><strong>Região geográfica</strong></li><li><strong>Faixa de valores</strong></li></ul><div class="tip custom-block"><p class="custom-block-title">Otimize seus filtros</p><ul><li>Use períodos específicos para relatórios mais rápidos</li><li>Combine filtros para análises focadas</li><li>Salve combinações frequentes como &quot;Filtros Favoritos&quot;</li></ul></div><h3 id="_4-personalizar-visualizacao" tabindex="-1">4. Personalizar visualização <a class="header-anchor" href="#_4-personalizar-visualizacao" aria-label="Permalink to &quot;4. Personalizar visualização&quot;">​</a></h3><p><strong>Opções de layout:</strong></p><ul><li><strong>Tabela detalhada:</strong> Todos os dados em linhas</li><li><strong>Resumo executivo:</strong> Apenas totais e métricas</li><li><strong>Gráfico:</strong> Visual com charts e dashboards</li><li><strong>Comparativo:</strong> Dois períodos lado a lado</li></ul><p><strong>Campos para exibir:</strong></p><ul><li>Marque apenas campos relevantes</li><li>Reordene colunas arrastando</li><li>Defina ordenação padrão</li></ul><h3 id="_5-gerar-relatorio" tabindex="-1">5. Gerar relatório <a class="header-anchor" href="#_5-gerar-relatorio" aria-label="Permalink to &quot;5. Gerar relatório&quot;">​</a></h3><ol><li>Clique em <strong>&quot;Gerar Relatório&quot;</strong></li><li>Aguarde o processamento (pode levar alguns segundos)</li><li>Visualize o resultado na tela</li></ol><div class="warning custom-block"><p class="custom-block-title">Relatórios grandes</p><p>Para relatórios com muito dados:</p><ul><li>Use filtros para reduzir volume</li><li>Execute em horários de menor uso</li><li>Considere dividir em períodos menores</li></ul></div><h3 id="_6-exportar-dados" tabindex="-1">6. Exportar dados <a class="header-anchor" href="#_6-exportar-dados" aria-label="Permalink to &quot;6. Exportar dados&quot;">​</a></h3><p><strong>Formatos disponíveis:</strong></p><h4 id="excel-xlsx" tabindex="-1">Excel (.xlsx) <a class="header-anchor" href="#excel-xlsx" aria-label="Permalink to &quot;Excel (.xlsx)&quot;">​</a></h4><ul><li><strong>Melhor para:</strong> Análises adicionais, tabelas dinâmicas</li><li><strong>Inclui:</strong> Formatação, fórmulas, múltiplas abas</li><li><strong>Limite:</strong> 1 milhão de linhas</li></ul><h4 id="pdf-pdf" tabindex="-1">PDF (.pdf) <a class="header-anchor" href="#pdf-pdf" aria-label="Permalink to &quot;PDF (.pdf)&quot;">​</a></h4><ul><li><strong>Melhor para:</strong> Apresentações, relatórios oficiais</li><li><strong>Inclui:</strong> Layout formatado, gráficos, cabeçalho/rodapé</li><li><strong>Opções:</strong> Orientação, tamanho da página</li></ul><h4 id="csv-csv" tabindex="-1">CSV (.csv) <a class="header-anchor" href="#csv-csv" aria-label="Permalink to &quot;CSV (.csv)&quot;">​</a></h4><ul><li><strong>Melhor para:</strong> Importação em outros sistemas</li><li><strong>Inclui:</strong> Apenas dados, sem formatação</li><li><strong>Vantagem:</strong> Arquivos menores, universalmente compatível</li></ul><h4 id="json-json" tabindex="-1">JSON (.json) <a class="header-anchor" href="#json-json" aria-label="Permalink to &quot;JSON (.json)&quot;">​</a></h4><ul><li><strong>Melhor para:</strong> Integrações técnicas, APIs</li><li><strong>Inclui:</strong> Dados estruturados programaticamente</li><li><strong>Uso:</strong> Desenvolvimento, automações</li></ul><h3 id="_7-salvar-configuracao-opcional" tabindex="-1">7. Salvar configuração (opcional) <a class="header-anchor" href="#_7-salvar-configuracao-opcional" aria-label="Permalink to &quot;7. Salvar configuração (opcional)&quot;">​</a></h3><ol><li>Clique em <strong>&quot;Salvar como Favorito&quot;</strong></li><li>Dê um nome descritivo</li><li>Escolha se quer compartilhar com equipe</li><li>Defina frequência de atualização automática</li></ol><h2 id="recursos-avancados" tabindex="-1">Recursos avançados <a class="header-anchor" href="#recursos-avancados" aria-label="Permalink to &quot;Recursos avançados&quot;">​</a></h2><h3 id="relatorios-agendados" tabindex="-1">Relatórios agendados <a class="header-anchor" href="#relatorios-agendados" aria-label="Permalink to &quot;Relatórios agendados&quot;">​</a></h3><ul><li>Configure execução automática (diária, semanal, mensal)</li><li>Defina lista de destinatários por e-mail</li><li>Escolha formato de entrega</li><li>Monitore histórico de envios</li></ul><h3 id="comparacao-de-periodos" tabindex="-1">Comparação de períodos <a class="header-anchor" href="#comparacao-de-periodos" aria-label="Permalink to &quot;Comparação de períodos&quot;">​</a></h3><ul><li>Mesmo mês do ano anterior</li><li>Período anterior equivalente</li><li>Meta vs. Realizado</li><li>Análise de tendências</li></ul><h3 id="drill-down" tabindex="-1">Drill-down <a class="header-anchor" href="#drill-down" aria-label="Permalink to &quot;Drill-down&quot;">​</a></h3><ul><li>Clique em valores para ver detalhes</li><li>Navegue entre níveis de informação</li><li>Filtre automaticamente por dimensão clicada</li></ul><h2 id="erros-comuns-e-solucoes" tabindex="-1">Erros comuns e soluções <a class="header-anchor" href="#erros-comuns-e-solucoes" aria-label="Permalink to &quot;Erros comuns e soluções&quot;">​</a></h2><h3 id="nenhum-dado-encontrado" tabindex="-1">&quot;Nenhum dado encontrado&quot; <a class="header-anchor" href="#nenhum-dado-encontrado" aria-label="Permalink to &quot;&quot;Nenhum dado encontrado&quot;&quot;">​</a></h3><p><strong>Possíveis causas:</strong></p><ul><li>Filtros muito restritivos</li><li>Período sem movimentação</li><li>Permissões insuficientes</li></ul><p><strong>Soluções:</strong></p><ul><li>Amplie o período de busca</li><li>Remova alguns filtros</li><li>Verifique permissões com administrador</li></ul><h3 id="timeout-na-geracao" tabindex="-1">&quot;Timeout na geração&quot; <a class="header-anchor" href="#timeout-na-geracao" aria-label="Permalink to &quot;&quot;Timeout na geração&quot;&quot;">​</a></h3><p><strong>Causa:</strong> Relatório muito complexo/grande <strong>Soluções:</strong></p><ul><li>Reduza período analisado</li><li>Use filtros mais específicos</li><li>Tente em horário de menor uso</li><li>Entre em contato com suporte</li></ul><h3 id="erro-de-exportacao" tabindex="-1">&quot;Erro de exportação&quot; <a class="header-anchor" href="#erro-de-exportacao" aria-label="Permalink to &quot;&quot;Erro de exportação&quot;&quot;">​</a></h3><p><strong>Causa:</strong> Problema temporário do sistema <strong>Soluções:</strong></p><ul><li>Tente novamente em alguns minutos</li><li>Use formato de arquivo diferente</li><li>Verifique se há espaço no navegador/computador</li></ul><div class="tip custom-block"><p class="custom-block-title">Boas práticas</p><ul><li><strong>Nomeação:</strong> Use nomes descritivos para relatórios salvos</li><li><strong>Frequência:</strong> Não execute o mesmo relatório várias vezes seguidas</li><li><strong>Compartilhamento:</strong> Evite enviar arquivos muito grandes por e-mail</li><li><strong>Backup:</strong> Mantenha cópias de relatórios importantes</li></ul></div><h2 id="interpretando-os-dados" tabindex="-1">Interpretando os dados <a class="header-anchor" href="#interpretando-os-dados" aria-label="Permalink to &quot;Interpretando os dados&quot;">​</a></h2><h3 id="metricas-principais" tabindex="-1">Métricas principais <a class="header-anchor" href="#metricas-principais" aria-label="Permalink to &quot;Métricas principais&quot;">​</a></h3><ul><li><strong>Total:</strong> Soma de valores no período</li><li><strong>Média:</strong> Valor médio por registro</li><li><strong>Crescimento:</strong> Percentual vs. período anterior</li><li><strong>Participação:</strong> % do item no total geral</li></ul><h3 id="indicadores-visuais" tabindex="-1">Indicadores visuais <a class="header-anchor" href="#indicadores-visuais" aria-label="Permalink to &quot;Indicadores visuais&quot;">​</a></h3><ul><li>📈 <strong>Verde:</strong> Crescimento positivo</li><li>📉 <strong>Vermelho:</strong> Queda/resultado negativo</li><li>➡️ <strong>Azul:</strong> Estabilidade/neutro</li><li>⚠️ <strong>Amarelo:</strong> Atenção/meta não atingida</li></ul><h2 id="proximos-passos" tabindex="-1">Próximos passos <a class="header-anchor" href="#proximos-passos" aria-label="Permalink to &quot;Próximos passos&quot;">​</a></h2><p>Após gerar seus relatórios:</p><ol><li><strong>Analise padrões</strong> e tendências identificadas</li><li><strong>Compartilhe insights</strong> com equipe relevante</li><li><strong>Agende relatórios recorrentes</strong> para acompanhamento</li><li><strong>Configure alertas</strong> para valores críticos</li></ol><p><strong>Dúvidas?</strong> Consulte <a href="/nextgoup/faq/#relatorios-e-exportacao">FAQ sobre Relatórios</a>.</p>',66)])])}const p=a(s,[["render",t]]);export{m as __pageData,p as default};
