import{_ as a,c as e,o as i,ag as r}from"./chunks/framework.BgN_SlIJ.js";const p=JSON.parse('{"title":"Criar Usuário","description":"","frontmatter":{},"headers":[],"relativePath":"tasks/criar-usuario.md","filePath":"tasks/criar-usuario.md","lastUpdated":null}'),s={name:"tasks/criar-usuario.md"};function t(l,o,n,u,c,d){return i(),e("div",null,[...o[0]||(o[0]=[r('<h1 id="criar-usuario" tabindex="-1">Criar Usuário <a class="header-anchor" href="#criar-usuario" aria-label="Permalink to &quot;Criar Usuário&quot;">​</a></h1><p>Esta página orienta administradores na criação de novos usuários no sistema Nextgoup.</p><h2 id="quem-pode-executar-esta-tarefa" tabindex="-1">Quem pode executar esta tarefa? <a class="header-anchor" href="#quem-pode-executar-esta-tarefa" aria-label="Permalink to &quot;Quem pode executar esta tarefa?&quot;">​</a></h2><ul><li>✅ <strong>Administrador:</strong> Acesso completo</li><li>❌ <strong>Gestor:</strong> Sem permissão</li><li>❌ <strong>Operador:</strong> Sem permissão</li></ul><p><strong>Tempo médio de execução:</strong> 3-5 minutos</p><h2 id="passo-a-passo" tabindex="-1">Passo a passo <a class="header-anchor" href="#passo-a-passo" aria-label="Permalink to &quot;Passo a passo&quot;">​</a></h2><h3 id="_1-acessar-o-modulo-de-usuarios" tabindex="-1">1. Acessar o módulo de usuários <a class="header-anchor" href="#_1-acessar-o-modulo-de-usuarios" aria-label="Permalink to &quot;1. Acessar o módulo de usuários&quot;">​</a></h3><ol><li>No menu lateral, clique em <strong>&quot;Usuários&quot;</strong></li><li>Você verá a lista de usuários existentes</li><li>Clique no botão <strong>&quot;+ Novo Usuário&quot;</strong> (canto superior direito)</li></ol><h3 id="_2-preencher-informacoes-basicas" tabindex="-1">2. Preencher informações básicas <a class="header-anchor" href="#_2-preencher-informacoes-basicas" aria-label="Permalink to &quot;2. Preencher informações básicas&quot;">​</a></h3><p><strong>Campos obrigatórios:</strong></p><ul><li><strong>Nome completo:</strong> Digite o nome real do usuário</li><li><strong>E-mail:</strong> Use o e-mail corporativo oficial</li><li><strong>Perfil:</strong> Selecione o nível de acesso apropriado</li><li><strong>Departamento:</strong> Escolha da lista pré-cadastrada</li></ul><p><strong>Campos opcionais:</strong></p><ul><li><strong>Telefone:</strong> Para contato direto</li><li><strong>Cargo:</strong> Posição na empresa</li><li><strong>Observações:</strong> Notas internas sobre o usuário</li></ul><div class="tip custom-block"><p class="custom-block-title">Escolhendo o perfil correto</p><ul><li><strong>Administrador:</strong> Para TI e gestores de sistema</li><li><strong>Gestor:</strong> Para supervisores e coordenadores</li><li><strong>Operador:</strong> Para usuários finais básicos</li></ul><p>Consulte <a href="/nextgoup/roles/administrador.html">Perfis de Usuário</a> para detalhes completos.</p></div><h3 id="_3-definir-configuracoes-de-acesso" tabindex="-1">3. Definir configurações de acesso <a class="header-anchor" href="#_3-definir-configuracoes-de-acesso" aria-label="Permalink to &quot;3. Definir configurações de acesso&quot;">​</a></h3><ul><li><strong>Senha temporária:</strong> O sistema gera automaticamente</li><li><strong>Forçar troca no primeiro login:</strong> Deixe marcado (recomendado)</li><li><strong>Data de expiração:</strong> Opcional, para contas temporárias</li><li><strong>Status:</strong> Mantenha como &quot;Ativo&quot;</li></ul><h3 id="_4-configurar-permissoes-especificas" tabindex="-1">4. Configurar permissões específicas <a class="header-anchor" href="#_4-configurar-permissoes-especificas" aria-label="Permalink to &quot;4. Configurar permissões específicas&quot;">​</a></h3><p>Dependendo do perfil escolhido, você pode:</p><ul><li><strong>Habilitar módulos específicos</strong></li><li><strong>Definir limitações de acesso</strong></li><li><strong>Configurar aprovações que o usuário pode fazer</strong></li></ul><h3 id="_5-salvar-e-enviar-credenciais" tabindex="-1">5. Salvar e enviar credenciais <a class="header-anchor" href="#_5-salvar-e-enviar-credenciais" aria-label="Permalink to &quot;5. Salvar e enviar credenciais&quot;">​</a></h3><ol><li>Clique em <strong>&quot;Salvar Usuário&quot;</strong></li><li>Confirme os dados na tela de revisão</li><li>Clique em <strong>&quot;Confirmar e Enviar E-mail&quot;</strong></li></ol><div class="warning custom-block"><p class="custom-block-title">Validações do sistema</p><p>O sistema verificará:</p><ul><li>E-mail único (não pode haver duplicatas)</li><li>Formato válido do e-mail</li><li>Preenchimento dos campos obrigatórios</li><li>Permissões do seu perfil para criar usuários</li></ul></div><h2 id="o-que-acontece-apos-a-criacao" tabindex="-1">O que acontece após a criação? <a class="header-anchor" href="#o-que-acontece-apos-a-criacao" aria-label="Permalink to &quot;O que acontece após a criação?&quot;">​</a></h2><h3 id="e-mail-automatico-enviado" tabindex="-1">E-mail automático enviado <a class="header-anchor" href="#e-mail-automatico-enviado" aria-label="Permalink to &quot;E-mail automático enviado&quot;">​</a></h3><p>O novo usuário receberá um e-mail contendo:</p><ul><li><strong>Link de acesso ao sistema</strong></li><li><strong>E-mail de login</strong></li><li><strong>Senha temporária</strong></li><li><strong>Instruções de primeiro acesso</strong></li><li><strong>Link para este manual</strong></li></ul><h3 id="status-do-usuario" tabindex="-1">Status do usuário <a class="header-anchor" href="#status-do-usuario" aria-label="Permalink to &quot;Status do usuário&quot;">​</a></h3><ul><li><strong>Status:</strong> Pendente → Ativo (após primeiro login)</li><li><strong>Última atividade:</strong> Será registrada quando fizer login</li><li><strong>Tentativas de login:</strong> Contador zerado</li></ul><h2 id="erros-comuns-e-solucoes" tabindex="-1">Erros comuns e soluções <a class="header-anchor" href="#erros-comuns-e-solucoes" aria-label="Permalink to &quot;Erros comuns e soluções&quot;">​</a></h2><h3 id="e-mail-ja-cadastrado" tabindex="-1">&quot;E-mail já cadastrado&quot; <a class="header-anchor" href="#e-mail-ja-cadastrado" aria-label="Permalink to &quot;&quot;E-mail já cadastrado&quot;&quot;">​</a></h3><p><strong>Causa:</strong> Tentativa de criar usuário com e-mail existente <strong>Solução:</strong></p><ul><li>Verifique se o usuário já existe na lista</li><li>Use um e-mail alternativo</li><li>Reative conta desabilitada se necessário</li></ul><h3 id="permissao-insuficiente" tabindex="-1">&quot;Permissão insuficiente&quot; <a class="header-anchor" href="#permissao-insuficiente" aria-label="Permalink to &quot;&quot;Permissão insuficiente&quot;&quot;">​</a></h3><p><strong>Causa:</strong> Seu perfil não permite criar usuários <strong>Solução:</strong></p><ul><li>Confirme que você é Administrador</li><li>Contate outro administrador para fazer a criação</li></ul><h3 id="erro-no-envio-do-e-mail" tabindex="-1">&quot;Erro no envio do e-mail&quot; <a class="header-anchor" href="#erro-no-envio-do-e-mail" aria-label="Permalink to &quot;&quot;Erro no envio do e-mail&quot;&quot;">​</a></h3><p><strong>Causa:</strong> Problema com servidor de e-mail <strong>Solução:</strong></p><ul><li>Usuário foi criado, mas e-mail não foi enviado</li><li>Envie credenciais manualmente ou reenvie pelo sistema</li><li>Verifique configurações de SMTP com TI</li></ul><h2 id="pos-criacao-acompanhamento" tabindex="-1">Pós-criação: acompanhamento <a class="header-anchor" href="#pos-criacao-acompanhamento" aria-label="Permalink to &quot;Pós-criação: acompanhamento&quot;">​</a></h2><h3 id="verificar-se-o-usuario-acessou" tabindex="-1">Verificar se o usuário acessou <a class="header-anchor" href="#verificar-se-o-usuario-acessou" aria-label="Permalink to &quot;Verificar se o usuário acessou&quot;">​</a></h3><ol><li>Vá em <strong>Usuários &gt; Lista de Usuários</strong></li><li>Procure pelo usuário criado</li><li>Verifique a coluna &quot;Último Acesso&quot;</li><li>Se necessário, entre em contato para auxiliar</li></ol><h3 id="solicitar-feedback" tabindex="-1">Solicitar feedback <a class="header-anchor" href="#solicitar-feedback" aria-label="Permalink to &quot;Solicitar feedback&quot;">​</a></h3><ul><li>Confirme se o usuário recebeu o e-mail</li><li>Verifique se conseguiu fazer login</li><li>Pergunte se precisa de treinamento adicional</li></ul><div class="tip custom-block"><p class="custom-block-title">Boa prática</p><p>Crie uma checklist para novos usuários:</p><ul><li>[ ] Usuário recebeu credenciais</li><li>[ ] Fez primeiro login com sucesso</li><li>[ ] Alterou senha temporária</li><li>[ ] Conhece funcionalidades do seu perfil</li><li>[ ] Sabe onde buscar ajuda</li></ul></div><h2 id="proximos-passos" tabindex="-1">Próximos passos <a class="header-anchor" href="#proximos-passos" aria-label="Permalink to &quot;Próximos passos&quot;">​</a></h2><p>Após criar o usuário:</p><ol><li><strong><a href="/nextgoup/roles/administrador.html">Definir permissões específicas</a></strong> se necessário</li><li><strong>Orientar sobre <a href="/nextgoup/getting-started/01-login-acesso.html">primeiros passos</a></strong></li><li><strong>Agendar treinamento</strong> conforme complexidade do perfil</li></ol><p><strong>Dúvidas?</strong> Consulte <a href="/nextgoup/faq/#gestao-de-usuarios">FAQ sobre Gestão de Usuários</a>.</p>',48)])])}const g=a(s,[["render",t]]);export{p as __pageData,g as default};
